import lixeira from "./assets/images/lixeira.png";
import { useState } from "react";

export default function App() {
  const [task, setTask] = useState([]);

  // adicionando tarefas
  function handleAddTask(item) {
    setTask((items) => [...items, item]);
  }

  // apagando tarefas
  function handleDeleteTask(id) {
    setTask((items) => items.filter((item) => item.id !== id));
  }

  return (
    <div className="app">
      <Header />
      <FormTasks task={task} setTask={setTask} onAddTask={handleAddTask} />
      <TasksList task={task} onDeleteTask={handleDeleteTask} />
      <Footer />
    </div>
  );
}

function Header() {
  return (
    <>
      <h1 className="title">To do List</h1>;
      <div className="custom-shape-divider-top-1714657111">
        <svg
          data-name="Layer 1"
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 1200 120"
          preserveAspectRatio="none"
        >
          <path
            d="M985.66,92.83C906.67,72,823.78,31,743.84,14.19c-82.26-17.34-168.06-16.33-250.45.39-57.84,11.73-114,31.07-172,41.86A600.21,600.21,0,0,1,0,27.35V120H1200V95.8C1132.19,118.92,1055.71,111.31,985.66,92.83Z"
            className="shape-fill"
          ></path>
        </svg>
      </div>
    </>
  );
}

function FormTasks({ onAddTask }) {
  const [inputTask, setInputTask] = useState("");

  function handleSubmit(e) {
    e.preventDefault();

    if (!inputTask) return;

    const newTask = { id: Date.now(), task: inputTask, completed: false };

    onAddTask(newTask);

    setInputTask("");
  }

  return (
    <form onSubmit={handleSubmit}>
      <label>Digite a tarefa a ser executada</label>
      <input
        type="text"
        value={inputTask}
        onChange={(e) => setInputTask(e.target.value)}
      />
      <button className="btn-add">Adicionar</button>
    </form>
  );
}

function TasksList({ task, onDeleteTask }) {
  const tarefas = task;

  return (
    <ul>
      {tarefas.map((item, i) => (
        <Tasks item={item} key={i} onClick={onDeleteTask} />
      ))}
      {tarefas.length > 0 && <button className="btn-reset">Reset</button>}
    </ul>
  );
}

